package org.opentutorials.javatutorials.condition;

public class Condition1Demo {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		if(true) {
			System.out.println("result : ture");
		} //　true 이기에 {} 가 실행된다.
		if(false) {
			System.out.println("result : ture");
		} // false 이기에 {} 가 실행되지 않는다.
	}
}
