package org.opentutorials.javatutorials.condition;

public class Condition3Demo {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		if (true) {
			System.out.println(1);
		} else {
			System.out.println(2);
		}
		
		if (false) {
			System.out.println(1);
		} else {
			System.out.println(2);
		}
	}

}
