package org.opentutorials.javatutorials.variables;

public class IntergerDemo {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int a; // 정수형 데이터의 변수 a (변수이름이 a인 정수형 데이터)
		a = 1; //　a라는 변수에 1을 할당하겠다. ( = 대입연산자 우항의 값을 좌항에 치환)
		System.out.println(a + 1); // 1 + 1 = 2
		
		a = 2; // 변수 초기화 a라는 변수에 2를 할당하겠다.
		System.out.println(a + 1); // 2 + 1 = 3
	}

}
