package org.opentutorials.javatutorials.variables;

public class StringDemo {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		String first; // 문자형 데이터 변수 first
		first = "coding"; // 변수의 초기화
		
		String second = "coding"; // 변수의 선언과 초기화
		
		String a = "coding";
		String b = "everybody";
		System.out.println(a + b); // 문자열끼리 연산자를 사용해서 이어줄 수 있다.
	}

}
